import React, { useState } from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';  // Import Quill styles
import './PostJobForm.css';

const PostJobForm = () => {
  const [formData, setFormData] = useState({
    job_title: '',
    company_name: '',
    location: '',
    postal_code: '',  // Add zip code field
    employment_type: 'full-time',
    remote_type: 'on-site',
    description: '',  // This will now store HTML
    salary_range: '',
    application_email: '',
    application_link: '',
    company_url: '',
    job_function: 'labor' // Changed default value to match enum
  });

  // Quill editor modules/formats configuration
  const modules = {
    toolbar: [
      [{ 'header': [1, 2, 3, false] }],
      ['bold', 'italic', 'underline'],
      [{'list': 'ordered'}, {'list': 'bullet'}],
      ['link'],
      ['clean']
    ],
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleDescriptionChange = (content) => {
    setFormData(prevState => ({
      ...prevState,
      description: content
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      const response = await fetch('http://localhost:8000/api/v1/jobs', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData)
      });

      if (response.ok) {
        alert('Job posted successfully!');
        // Could redirect to homepage or job listing
      } else {
        alert('Failed to post job. Please try again.');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('Error posting job. Please try again.');
    }
  };

  return (
    <div className="post-job-container">
      <h2>Post a Job</h2>
      <form onSubmit={handleSubmit} className="post-job-form">
        <div className="form-group">
          <label htmlFor="job_title">Job Title *</label>
          <input
            type="text"
            id="job_title"
            name="job_title"
            value={formData.job_title}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="company_name">Company Name *</label>
          <input
            type="text"
            id="company_name"
            name="company_name"
            value={formData.company_name}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-row">
          <div className="form-group">
            <label htmlFor="location">City, State *</label>
            <input
              type="text"
              id="location"
              name="location"
              value={formData.location}
              onChange={handleChange}
              required
              placeholder="e.g. Austin, TX"
            />
          </div>

          <div className="form-group">
            <label htmlFor="postal_code">ZIP Code *</label>
            <input
              type="text"
              id="postal_code"
              name="postal_code"
              value={formData.postal_code}
              onChange={handleChange}
              required
              pattern="[0-9]{5}"
              placeholder="e.g. 78701"
            />
          </div>
        </div>

        <div className="form-row">
          <div className="form-group">
            <label htmlFor="job_function">Job Function *</label>
            <select
              id="job_function"
              name="job_function"
              value={formData.job_function}
              onChange={handleChange}
              required
            >
              <option value="sales">Sales</option>
              <option value="labor">Labor</option>
              <option value="production">Production</option>
              <option value="management">Management</option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="employment_type">Employment Type</label>
            <select
              id="employment_type"
              name="employment_type"
              value={formData.employment_type}
              onChange={handleChange}
            >
              <option value="full-time">Full Time</option>
              <option value="part-time">Part Time</option>
              <option value="contract">Contract</option>
              <option value="temporary">Temporary</option>
            </select>
          </div>
        </div>

        <div className="form-group">
          <label htmlFor="remote_type">Work Type</label>
          <select
            id="remote_type"
            name="remote_type"
            value={formData.remote_type}
            onChange={handleChange}
          >
            <option value="on-site">On Site</option>
            <option value="remote">Remote</option>
            <option value="hybrid">Hybrid</option>
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="description">Job Description *</label>
          <ReactQuill
            theme="snow"
            value={formData.description}
            onChange={handleDescriptionChange}
            modules={modules}
            className="quill-editor"
          />
        </div>

        <div className="form-group">
          <label htmlFor="salary_range">Salary Range</label>
          <input
            type="text"
            id="salary_range"
            name="salary_range"
            value={formData.salary_range}
            onChange={handleChange}
            placeholder="e.g. $50,000 - $70,000"
          />
        </div>

        <div className="form-group">
          <label htmlFor="application_email">Application Email</label>
          <input
            type="email"
            id="application_email"
            name="application_email"
            value={formData.application_email}
            onChange={handleChange}
          />
        </div>

        <div className="form-group">
          <label htmlFor="application_link">Application URL</label>
          <input
            type="url"
            id="application_link"
            name="application_link"
            value={formData.application_link}
            onChange={handleChange}
            placeholder="https://"
          />
        </div>

        <div className="form-group">
          <label htmlFor="company_url">Company Website</label>
          <input
            type="url"
            id="company_url"
            name="company_url"
            value={formData.company_url}
            onChange={handleChange}
            placeholder="https://"
          />
        </div>

        <button type="submit" className="submit-button">
          Post Job
        </button>
      </form>
    </div>
  );
};

export default PostJobForm;
