import React from 'react';
import './JobCard.css';

const JobCard = ({ job, onClick }) => {
  // Function to strip HTML tags and get plain text
  const stripHtml = (html) => {
    const tmp = document.createElement('div');
    tmp.innerHTML = html;
    return tmp.textContent || tmp.innerText || '';
  };

  // Get a preview of the description (first 150 chars, without HTML tags)
  const getDescriptionPreview = () => {
    const plainText = stripHtml(job.description || '');
    return plainText.substring(0, 150) + (plainText.length > 150 ? '...' : '');
  };

  return (
    <div className="job-card" onClick={() => onClick(job)}>
      <h3 className="job-title">{job.job_title}</h3>
      <div className="job-info">
        <p className="location">{job.location}</p>
        {job.salary_range && <p className="salary">{job.salary_range}</p>}
      </div>
      <div className="job-type">
        <span className="employment-type">{job.employment_type}</span>
        <span className="remote-type">{job.remote_type}</span>
      </div>
      <p className="description">{getDescriptionPreview()}</p>
      <button className="view-details">View Details</button>
    </div>
  );
};

export default JobCard;
