// src/App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import JobList from './components/JobList';
import Filters from './components/Filters';
import LocationSelector from './components/LocationSelector';
import Header from './components/Header';
import JobModal from './components/JobModal';
import PostJobForm from './components/PostJobForm';
import './app.css';

const HomePage = ({ jobs, filteredJobs, handleFilterChange, handleLocationChange, handleJobClick, error, isLoading }) => (
    <main className="main-content">
        <div className="search-section">
            <LocationSelector onLocationChange={handleLocationChange} />
            <div className="filters">
                <select 
                    onChange={(e) => handleFilterChange({ type: 'job_function', value: e.target.value })}
                    defaultValue=""
                >
                    <option value="">All Job Functions</option>
                    <option value="sales">Sales</option>
                    <option value="labor">Labor</option>
                    <option value="production">Production</option>
                    <option value="management">Management</option>
                </select>
            </div>
            <Filters onFilterChange={handleFilterChange} />
        </div>
        <div className="jobs-container">
            {error ? (
                <div className="error-message">
                    <h3>Unable to connect to job server</h3>
                    <p>Please try again later. The server might be temporarily down.</p>
                </div>
            ) : isLoading ? (
                <div className="loading">
                    <h3>Searching for jobs...</h3>
                </div>
            ) : filteredJobs.length > 0 ? (
                <JobList jobs={filteredJobs} onJobClick={handleJobClick} />
            ) : (
                <div className="no-jobs">
                    <h3>No jobs available at the moment</h3>
                    <p>Please check back later or try adjusting your search criteria.</p>
                </div>
            )}
        </div>
    </main>
);

const App = () => {
    const [jobs, setJobs] = useState([]);
    const [filteredJobs, setFilteredJobs] = useState([]);
    const [selectedJob, setSelectedJob] = useState(null);
    const [error, setError] = useState(null);
    const [isLoading, setIsLoading] = useState(false);

    useEffect(() => {
        const fetchJobs = async () => {
            try {
                const response = await fetch('http://localhost:8000/api/v1/jobs');
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const data = await response.json();
                setJobs(data);
                setFilteredJobs(data);
                setError(null);
            } catch (error) {
                console.error('Error fetching jobs:', error);
                setError(error.message);
                setJobs([]);
                setFilteredJobs([]);
            }
        };

        fetchJobs();
    }, []);

    const handleFilterChange = (filter) => {
        if (filter.type === 'job_function') {
            const newFilteredJobs = jobs.filter(job => job.job_function === filter.value);
            setFilteredJobs(newFilteredJobs);
        } else {
            const newFilteredJobs = jobs.filter(job => job.type === filter.value);
            setFilteredJobs(newFilteredJobs);
        }
    };

    const handleLocationChange = async (locationFilter) => {
        setIsLoading(true);
        try {
            if (!locationFilter) {
                // If no location filter, show all jobs
                setFilteredJobs(jobs);
                setError(null);
            } else {
                const { zipCode, radius } = locationFilter;
                const response = await fetch(
                    `http://localhost:8000/api/v1/jobs/search/location?zip_code=${zipCode}&radius=${radius}`
                );
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const data = await response.json();
                setFilteredJobs(data);
                setError(null);
            }
        } catch (error) {
            console.error('Error searching jobs:', error);
            setError(error.message);
            setFilteredJobs([]);
        } finally {
            setIsLoading(false);
        }
    };

    const handleJobClick = (job) => {
        setSelectedJob(job);
    };

    const handleCloseModal = () => {
        setSelectedJob(null);
    };

    return (
        <Router>
            <div className="app-container">
                <Header />
                <Routes>
                    <Route path="/" element={
                        <HomePage 
                            jobs={jobs}
                            filteredJobs={filteredJobs}
                            handleFilterChange={handleFilterChange}
                            handleLocationChange={handleLocationChange}
                            handleJobClick={handleJobClick}
                            error={error}
                            isLoading={isLoading}
                        />
                    } />
                    <Route path="/post-job" element={<PostJobForm />} />
                </Routes>
                {selectedJob && <JobModal job={selectedJob} onClose={handleCloseModal} />}
            </div>
        </Router>
    );
};

export default App;
