// src/components/LocationSelector.js
import React, { useState, useEffect } from 'react';

const LocationSelector = ({ onLocationChange }) => {
    const [zipCode, setZipCode] = useState('');
    const [radius, setRadius] = useState('25');

    const handleSubmit = (e) => {
        e.preventDefault();
        onLocationChange(zipCode.trim() ? { zipCode, radius: parseFloat(radius) } : null);
    };

    return (
        <div className="location-selector">
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="Enter ZIP code (optional)"
                    value={zipCode}
                    onChange={e => setZipCode(e.target.value)}
                    pattern="[0-9]{5}|^$"
                    title="Please enter a valid 5-digit ZIP code or leave empty to show all jobs"
                />
                <select
                    value={radius}
                    onChange={e => setRadius(e.target.value)}
                >
                    <option value="25"><span className="radius-text">Within 25 miles</span><span className="radius-mobile"><i className="fas fa-car"></i> 25 mi</span></option>
                    <option value="50"><span className="radius-text">Within 50 miles</span><span className="radius-mobile"><i className="fas fa-car"></i> 50 mi</span></option>
                    <option value="100"><span className="radius-text">Within 100 miles</span><span className="radius-mobile"><i className="fas fa-car"></i> 100 mi</span></option>
                    <option value="150"><span className="radius-text">Within 150 miles</span><span className="radius-mobile"><i className="fas fa-car"></i> 150 mi</span></option>
                    <option value="200"><span className="radius-text">Within 200 miles</span><span className="radius-mobile"><i className="fas fa-car"></i> 200 mi</span></option>
                </select>
                <button type="submit">
                    {zipCode.trim() ? 'Search Jobs' : 'Show All Jobs'}
                </button>
            </form>
        </div>
    );
};

export default LocationSelector;
