import React from 'react';
import './JobModal.css';

const JobModal = ({ job, onClose }) => {
  if (!job) return null;

  const formatJobFunction = (jobFunction) => {
    if (!jobFunction) return '';
    return jobFunction.charAt(0).toUpperCase() + jobFunction.slice(1).toLowerCase();
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={e => e.stopPropagation()}>
        <button className="modal-close" onClick={onClose}>&times;</button>
        
        <div className="modal-header">
          <h2>{job.job_title}</h2>
          {job.company_url && (
            <a href={job.company_url} target="_blank" rel="noopener noreferrer" className="company-link">
              Company Website
            </a>
          )}
        </div>

        <div className="modal-body">
          <div className="job-meta">
            <p className="location">{job.location}</p>
            {job.employment_type && <p className="employment-type">{job.employment_type}</p>}
            {job.remote_type && <p className="remote-type">{job.remote_type}</p>}
            {job.job_function && <p className="job-function">{formatJobFunction(job.job_function)}</p>}
          </div>

          <div className="job-description">
            <h3>Description</h3>
            <div 
              className="formatted-description"
              dangerouslySetInnerHTML={{ __html: job.description }}
            />
          </div>

          {job.application_link && (
            <a 
              href={job.application_link} 
              target="_blank" 
              rel="noopener noreferrer"
              className="apply-button"
            >
              Apply Now
            </a>
          )}
        </div>
      </div>
    </div>
  );
};

export default JobModal;
